
public class ArrayReverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a[] = {1,2,3,4,5,6,7};
		for(int i=a.length-1;i>=0;i--)
		{
			System.out.println(a[i]);
		}
		
		char c[] = {'a','b','c','d','e','f'};
		for (int i=c.length-1;i>=0;i--)
		{
			System.out.print(c[i]);
		}
	}

}
